import React, { useState} from 'react';
import { useHistory } from 'react-router-dom'
import { } from 'react-icons/fi';

import '../../assets/stylesheets/home.css';

const Home= () => {
    const [name, setName] = useState('0');
    const history = useHistory();

    function handleToHome(event) {
        setName(event.name);

        //criar url aleatoria ou com nome da pessoa, salvar nome da pessoa e ir para sala
        //passar nome da pessoa pelas props de room pra já estar logado no chat
        history.push('/room')
    }

    return (
        <div>
            <h1>
                VideoSynChat
            </h1>
            <h1 class="header">
                Welcome, put a name and create a room
            </h1>
            <div id="usernameInputFrame" class="input-group flex-nowrap">
                <input type="text" id="inputUsername" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="addon-wrapping"/>
            </div>
            <button id="createRoomButton" class="botton" onClick={handleToHome}>Create</button>
        </div>
    )
}

export default Home;
